import React, {useState, useEffect, useRef} from 'react';
import { View, Text, StyleSheet, ScrollView } from 'react-native';
import Logo from '../../assets/images/PersonIcon.png';
import CustomButton from '../../components/CustomButton/CustomButton';
import { useNavigation } from '@react-navigation/native';
import MicRecorder from "mic-recorder-to-mp3"
import { Oval } from "react-loader-spinner"
import axios from "axios"
import Audio1 from '../../assets/images/audio1.png';
import useSpeechToText, { ResultType } from './Hooks';


const assemblyAI = axios.create({
  baseURL: "https://api.assemblyai.com/v2",
  headers: {
    authorization: "6bbce7a69ff249d491a441d226023d83",
    "content-type": "application/json",
    "transfer-encoding": "chunked",
  },
})

const StudentScreen = () => {
  const navigation = useNavigation()

  const handleBack = () => {
    navigation.navigate('ChooseUser')
  }

  const recorder = useRef(null) //Recorder
  const audioPlayer = useRef(null) //Ref for the HTML Audio Tag
  const [blobURL, setBlobUrl] = useState(null)
  const [audioFile, setAudioFile] = useState(null)
  const [isRecording, setIsRecording] = useState(null)

  

  

  const handleImgClick = () => {
    navigation.navigate('QuestionScreen')
  }

  

  return (
    <View style={styles.root}>
      <br/>
      <Text style={styles.title}>Child Profile</Text>
      <br/>
      <img width="145" height="135" src={Logo} /> 
      <Text style={styles.text}>Today's Date: 10/29</Text>
      <br/>
      <img width="315" height="280" src={Audio1} onClick={handleImgClick} />

    <CustomButton text={"Go Back"} onPress={handleBack} type="SECONDARY"/>
    </View>
  )
}

const styles = StyleSheet.create({
  root: {
    alignItems: 'center',
    padding: 20,
    backgroundColor: '#364d59',
    height: '100%',
  },
  title: {
    fontSize: 36,
    fontWeight: 'bold',
    color: 'white',
  },
  text: {
    marginTop: "10px",
    fontSize: 20,
    color: 'white',
  },
  text2: {
    marginTop: "10px",
    fontSize: 20,
    color: 'white',
    fontWeight: 'bold',
  },
});

export default StudentScreen;
