import React, {useState, useEffect} from 'react';
import { View, Text, Image, StyleSheet, useWindowDimensions, ScrollView, TextInput } from 'react-native';
import CustomInput from '../../components/CustomInput/CustomInput';
import CustomButton from '../../components/CustomButton/CustomButton';
import { useNavigation } from '@react-navigation/native';
import {useForm, Controller} from 'react-hook-form';
import Audio1 from '../../assets/images/audio1.png';

const TodayScheduleScreen = () => {
  const navigation = useNavigation();
  const {control, watch} = useForm();
  const keyword = watch('keyword');

  const handleBack = () => {
    navigation.navigate('ParentScreen')
  }

  return (
    <View style={styles.root}>
      <br/>
      <Text style={styles.title}>Today's Schedule</Text>
      <br/>
      <div>
        <img width="50" height="45" src={Audio1}/>
        <Text style={styles.text}>    Action Item 1</Text>
        <CustomInput 
          name="keyword"
          placeholder="Keyword"
          secureTextEntry
          control={control}
          rules={{required: 'Password is required', minLength: {value: 8, message: 'Password is a minimum of 8 characters long'}}}
        />
      </div>
      <br/><br/>
      <CustomButton text={"Go Back"} onPress={handleBack} type="SECONDARY"/>
    </View>
  )
}

const styles = StyleSheet.create({
  root: {
    alignItems: 'center',
    padding: 20,
    backgroundColor: '#364d59',
    height: '100%',
  },
  title: {
    fontSize: 36,
    fontWeight: 'bold',
    color: 'white',
  },
  text: {
    marginTop: "10px",
    fontSize: 20,
    color: 'white',
    marginBottom: "48px"
  },
  text2: {
    marginTop: "10px",
    fontSize: 20,
    color: 'white',
    fontWeight: 'bold',
  },
});

export default TodayScheduleScreen;
