import React, {useState, useEffect, useRef} from 'react';
import { View, Text, StyleSheet, ScrollView } from 'react-native';
import Logo from '../../assets/images/PersonIcon.png';
import CustomButton from '../../components/CustomButton/CustomButton';
import { useNavigation } from '@react-navigation/native';
import MicRecorder from "mic-recorder-to-mp3"
import { Oval } from "react-loader-spinner"
import axios from "axios"
import Audio1 from '../../assets/images/audio1.png';
import useSpeechToText, { ResultType } from './Hooks';




const StudentScreen = () => {
  const navigation = useNavigation()

  const handleBack = () => {
    navigation.navigate('ChooseUser')
  }



  

  
  const handleImgClick = () => {
    navigation.navigate('QuestionScreen')
  }

  const {
    error,
    interimResult,
    isRecording,
    results,
    startSpeechToText,
    stopSpeechToText
  } = useSpeechToText({
    continuous: true,
    crossBrowser: true,
    timeout: 3000,
    googleApiKey: process.env.REACT_APP_API_KEY,
    speechRecognitionProperties: { interimResults: true },
    useLegacyResults: false
  });

  if (error) {
    return (
      <div
        style={{
          maxWidth: '600px',
          margin: '100px auto',
          textAlign: 'center'
        }}
      >
        <p>
          {error}
          <span style={{ fontSize: '3rem' }}>ü§∑‚Äç</span>
        </p>
      </div>
    );
  }

  return (
    <div
      style={{
        maxWidth: '600px',
        margin: '100px auto',
        textAlign: 'center'
      }}
    >
      <View style={styles.root}>
      <br/>
      <Text style={styles.title}>Child Profile</Text>
      <br/>
      <img width="145" height="135" src={Logo} /> 
      <Text style={styles.text}>Today's Date: 10/29</Text>
      <br/>
      <img width="315" height="280" src={Audio1} onClick={handleImgClick} />
    <CustomButton text={"Go Back"} onPress={handleBack} type="SECONDARY"/>
    </View>
      <h1>Recording: {isRecording.toString()}</h1>
      <button onClick={isRecording ? stopSpeechToText : startSpeechToText}>
        <span>{isRecording ? 'Stop Recording' : 'Start Recording'}</span>
      </button>
      <ul>
        {(results).map((result) => (
          <li key={result.timestamp}>{result.transcript}</li>
        ))}
        {interimResult && <li>{interimResult}</li>}
      </ul>
    </div>
  );
  
  
}
  

const styles = StyleSheet.create({
  root: {
    alignItems: 'center',
    padding: 20,
    backgroundColor: '#364d59',
    height: '100%',
  },
  title: {
    fontSize: 36,
    fontWeight: 'bold',
    color: 'white',
  },
  text: {
    marginTop: "10px",
    fontSize: 20,
    color: 'white',
  },
  text2: {
    marginTop: "10px",
    fontSize: 20,
    color: 'white',
    fontWeight: 'bold',
  },
});

export default StudentScreen;
